config.script:
root = self.getChild("root")

try:
	system.perspective.sendMessage('mhProviderDashboardKpiHeader', payload = {})
except:
	root.cleanKpis()

onChange.script:
root = self
if currentValue.value is True:
	payload = {}

	system.perspective.sendMessage('mhProviderDashboardKpiHeader', payload = payload)

else:
	root.cleanKpis()
