config.script:
selected_data = self.props.selection.data
eventId = selected_data[0]['id']
eventName = selected_data[0]['name']
selected_column = self.props.selection.selectedColumn
ActiveParticipant = selected_data[0]['IsActive']
if selected_column == 'id':
	if ActiveParticipant == 1:
		tab_id = 2 # registration
		system.perspective.navigate("Events/Detail/" + str(eventId) + "/" + str(tab_id))
	else:
		if self.session.custom.isAdministrator == True:
			id = "pop_event_set_active" 
			view = "Events/ParticipantEvents/Templates/PopupSetActive"
			parameters = {'eventId':eventId,'eventName':eventName}
			system.perspective.openPopup(id,view,parameters,showCloseIcon = False, modal = True)
else:
	if ActiveParticipant == 0:
