transforms.code:
	#Returns the next date that is that weekday
	#date: the datetime to start from
	#weekday: the weekday as a number
#	self.refreshBinding('custom.schedule')
	seasonStart = self.custom.RepeatStartDate
	#set backdate limit	
	if seasonStart > system.date.now():
		seasonStart = seasonStart
	else:
#		seasonStart = system.date.setTime(system.date.now(), system.date.getHour24(seasonStart), system.date.getMinute(seasonStart), system.date.getSecond(seasonStart))
		now = system.date.addDays(system.date.now(),-7)
		if seasonStart > now:
			seasonStart = seasonStart
		else:
			if self.params.IsUpdate == True:
				seasonStart = system.date.setTime(now, system.date.getHour24(seasonStart), system.date.getMinute(seasonStart), system.date.getSecond(seasonStart))
			else:
				seasonStart = seasonStart

	def nextDayOfWeek(date, weekday):
		dayOfWeek = system.date.getDayOfWeek(date)
		daysApart = weekday - dayOfWeek
		if daysApart < 0: daysApart = 7 + daysApart
		return system.date.addDays(date, daysApart)

	seasonStart = seasonStart #value['seasonStart']
	startDayOfWeek = system.date.getDayOfWeek(seasonStart)
	seasonEnd = value['seasonEnd']
	dates = []
	if seasonStart is not None and seasonEnd is not None:
		original_startDate = self.custom.RepeatStartDate
		if system.date.isBefore(seasonEnd, original_startDate): return [system.date.setTime(x, 00, 00, 00) for x in self.view.custom.PrevSchedule] #'ERROR: Season End before Start!!!'
		#Pattern Key: 1 = Every, 2 = Every Other, 3 = Every 1st ...7 = Every 5th
		pattern = self.custom.pattern
		#Weekdays Key: 1 = Sunday ... 7 = Saturday
		weekdays = value['weekdays']

		currentDate = system.date.format(system.date.now(),'MM-dd-yyyy')
		# set ends on time to eventEnd date, as end date need to include in shedule
		endsOn_timeFromDate = self.getChild("root").getChild("FlexContainer").getChild("EndsOn_Flex").getChild("Ends_On").props.params.date
		seasonEnd = str(system.date.format(seasonEnd,'yyyy-MM-dd ')) + str(system.date.format(endsOn_timeFromDate,'HH:mm:ss'))
		seasonEnd = system.date.parse(seasonEnd,'yyyy-MM-dd HH:mm:ss')
		#Every
		if pattern == 1:
			for weekday in weekdays:
				schedDay = nextDayOfWeek(seasonStart,weekday)
				while system.date.isBefore(schedDay, seasonEnd):
					dates.append(schedDay)
					schedDay = system.date.addDays(schedDay, 7)
		if pattern == 2:
			for weekday in weekdays:
				schedDay = nextDayOfWeek(seasonStart,weekday)
				while system.date.isBefore(schedDay, seasonEnd):
					dates.append(schedDay)
					schedDay = system.date.addDays(schedDay, 14)
		if pattern > 2 and pattern < 8:
			for weekday in weekdays:
				monthStart = system.date.getDate(system.date.getYear(seasonStart), system.date.getMonth(seasonStart), 1)
				dayOfWeek = nextDayOfWeek(monthStart, weekday)
				dayOfWeek = system.date.addDays(dayOfWeek, ((pattern-3)*7))
				while system.date.isBefore(dayOfWeek, seasonEnd):
					if system.date.isAfter(dayOfWeek, seasonStart) and system.date.getMonth(dayOfWeek)==system.date.getMonth(monthStart):
						dates.append(dayOfWeek)
					monthStart = system.date.addMonths(monthStart,1)
					dayOfWeek = nextDayOfWeek(monthStart, weekday)
					dayOfWeek = system.date.addDays(dayOfWeek, ((pattern-3)*7))		
		if pattern > 7:
			if pattern == 8:
				include_patterns = [3,5]
			if pattern == 9:
				include_patterns = [4,6]
			for pattern in include_patterns:
				for weekday in weekdays:
					monthStart = system.date.getDate(system.date.getYear(seasonStart), system.date.getMonth(seasonStart), 1)
					dayOfWeek = nextDayOfWeek(monthStart, weekday)
					dayOfWeek = system.date.addDays(dayOfWeek, ((pattern-3)*7))
					while system.date.isBefore(dayOfWeek, seasonEnd):
						if system.date.isAfter(dayOfWeek, seasonStart) and system.date.getMonth(dayOfWeek)==system.date.getMonth(monthStart):
							dates.append(dayOfWeek)
						monthStart = system.date.addMonths(monthStart,1)
						dayOfWeek = nextDayOfWeek(monthStart, weekday)
						dayOfWeek = system.date.addDays(dayOfWeek, ((pattern-3)*7))			

		# remove if it is repeate weekDays but cancle in database
		NewDates = []
		Cancel = False
		for oneDate in dates:
			Cancel = False
			if self.custom.pattern == self.custom.EventData.dateSelectionPatternDB and system.date.getDayOfWeek(oneDate) in (self.custom.EventData.dateSelectionDays):
				#only dates between start and endDate from db consider as cancel dates if that not found in db
				if system.date.isBefore(oneDate, self.custom.EventData.repeatDateUntil) and system.date.isAfter(oneDate, self.custom.EventData.startsOnDB):
					Cancel = True
					for prevDates in (self.custom.PrevSchedule):
						if system.date.format(prevDates,'yyyy-MM-dd') == system.date.format(oneDate,'yyyy-MM-dd'):
							Cancel = False
			if Cancel == False:
				NewDates.append(oneDate)
		dates = NewDates

		# append startDate if not added
		startsOn_original = self.getChild("root").getChild("FlexContainer").getChild("StartsOn_Date_Flex").getChild("Starts_On_Date").props.params.date
		if system.date.format(startsOn_original,'yyyy-MM-dd') not in [system.date.format(x,'yyyy-MM-dd') for x in dates]:
			dates.append(startsOn_original)

		# append scheduled dates from database
		result = system.db.runNamedQuery('Event/ParticipantEvents/Registration/Get_ScheduledEvents',{'eventId':self.params.event_id})	
		weekdays = self.custom.EventData.dateSelectionDays
		startDate = self.getChild("root").getChild("FlexContainer").getChild("StartsOn_Date_Flex").getChild("Starts_On_Date").props.params.date
		startDate_dayOfYear = system.date.format(self.getChild("root").getChild("FlexContainer").getChild("StartsOn_Date_Flex").getChild("Starts_On_Date").props.params.date,'yyyy-MM-dd hh:mm:ss')
		if result.getRowCount() > 0:
			for i in range(0,result.getRowCount()):
				startsOn = result.getValueAt(i,'startsOn')
				if 1 == 1:
				#if (system.date.getDayOfWeek(startsOn) not in weekdays) or (system.date.format(startsOn,'yyyy-MM-dd hh:mm:ss') == startDate_dayOfYear) or system.date.format(startsOn,'yyyy-MM-dd HH:mm:ss') < system.date.format(seasonStart,'yyyy-MM-dd HH:mm:ss'):
				#if (system.date.format(startsOn,'yyyy-MM-dd') not in [system.date.format(x,'yyyy-MM-dd') for x in dates]) or (system.date.format(startsOn,'yyyy-MM-dd hh:mm:ss') == startDate_dayOfYear):
					if system.date.format(startsOn,'yyyy-MM-dd') not in [system.date.format(x,'yyyy-MM-dd') for x in dates]:
						##select dates only between startDate and repeateUntil if anyone of these change
						repeateUntil_original = self.getChild("root").getChild("FlexContainer").getChild("Schedule_Flex").getChild("EndDate").getChild("Ends_On").props.params.date
						if system.date.isBetween(startsOn, startsOn_original, repeateUntil_original):
							if system.date.getDayOfWeek(startsOn) in self.custom.EventData.weekdays and system.date.getDayOfWeek(startsOn) in self.custom.EventData.weekdays_db and self.custom.pattern == self.custom.EventData.dateSelectionPatternDB:
								dates.append(startsOn)
							elif system.date.getDayOfWeek(startsOn) not in self.custom.EventData.weekdays_db:
								dates.append(startsOn)
	if self.params.IsUpdate	== False:
		dates.append(self.getChild("root").getChild("FlexContainer").getChild("StartsOn_Date_Flex").getChild("Starts_On_Date").props.params.date)
	return dates

