onChange.script:
	if previousValue != None and currentValue.value != None and currentValue.value != '':
		self.parent.parent.getChild("Schedule_Flex").getChild("Calendar").getChild("Table").props.params.startDate = currentValue.value
		duration = self.parent.parent.getChild("Duration_Flex").getChild("Duration").props.params.value
