		scheduleToRemove = previousValue.value #system.date.getDayOfYear(previousValue.value) 
		scheduleToAdd = currentValue.value #system.date.getDayOfYear(currentValue.value)
		if self.view.custom.PrevStartsOn != None and self.view.custom.PrevStartsOn != '':
			if system.date.format(currentValue.value,'yyyy-MM-dd') != system.date.format(scheduleToRemove,'yyyy-MM-dd'):
