		def updateCalenderFiller(scheduleToAdd,scheduleToRemove):
			StartsOn_Found = False
			calenderFiller = self.view.custom.calendarFiller
			for date in calenderFiller:
				if scheduleToRemove != None and scheduleToRemove != '':
					if system.date.format(date,'yyyy-MM-dd') == system.date.format(scheduleToRemove,'yyyy-MM-dd'):
						if scheduleToAdd != None:
							if system.date.format(scheduleToAdd,'yyyy-MM-dd') != system.date.format(scheduleToRemove,'yyyy-MM-dd'):
								# remove date only if removeDate is smaller than startsOn OR removeDate is greater & dayOfWeek of removeDate not in weekDays
								if (scheduleToRemove<scheduleToAdd or (scheduleToRemove>scheduleToAdd and system.date.getDayOfWeek(scheduleToRemove) not in self.view.custom.EventData.weekdays)):
									(self.view.custom.calendarFiller).remove(date)
						else:
							(self.view.custom.calendarFiller).remove(date)
				if scheduleToAdd != None:
					if system.date.format(date,'yyyy-MM-dd') == system.date.format(scheduleToAdd,'yyyy-MM-dd'):
						StartsOn_Found = True
			if scheduleToAdd != None and StartsOn_Found == False:
				(self.view.custom.calendarFiller).append(scheduleToAdd)
		# add current startsOn and remove previous startsOn
		updateCalenderFiller(scheduleToAdd,scheduleToRemove)

		##
		if currentValue.value > previousValue.value:
			seasonStart = previousValue.value
			seasonEnd = currentValue.value
		else:
			seasonStart = currentValue.value
			seasonEnd = previousValue.value 
		def nextDayOfWeek(date, weekday):
			dayOfWeek = system.date.getDayOfWeek(date)
			daysApart = weekday - dayOfWeek
			if daysApart < 0: daysApart = 7 + daysApart
			return system.date.addDays(date, daysApart)
		startDayOfWeek = system.date.getDayOfWeek(seasonStart)
		pattern = self.view.custom.pattern
		weekdays = self.view.custom.EventData.weekdays
		dates = []
		currentDate = system.date.format(system.date.now(),'MM-dd-yyyy')
		#Every
		if pattern == 1:
			for weekday in weekdays:
				schedDay = nextDayOfWeek(seasonStart,weekday)
				while system.date.isBefore(schedDay, seasonEnd):
					if currentValue.value > previousValue.value:
						updateCalenderFiller(None,schedDay)
					else:
						updateCalenderFiller(schedDay,None)
					schedDay = system.date.addDays(schedDay, 7)
		if pattern == 2:
			for weekday in weekdays:
				schedDay = nextDayOfWeek(seasonStart,weekday)
				while system.date.isBefore(schedDay, seasonEnd):
					if currentValue.value > previousValue.value:
						updateCalenderFiller(None,schedDay)
					else:
						updateCalenderFiller(schedDay,None)
					schedDay = system.date.addDays(schedDay, 14)
		if pattern > 2:
			for weekday in weekdays:
				monthStart = system.date.getDate(system.date.getYear(seasonStart), system.date.getMonth(seasonStart), 1)
				dayOfWeek = nextDayOfWeek(monthStart, weekday)
				dayOfWeek = system.date.addDays(dayOfWeek, ((pattern-3)*7))
				while system.date.isBefore(dayOfWeek, seasonEnd):
					if system.date.isAfter(dayOfWeek, seasonStart) and system.date.getMonth(dayOfWeek)==system.date.getMonth(monthStart):
						if currentValue.value > previousValue.value:
							updateCalenderFiller(None,dayOfWeek)
						else:
							updateCalenderFiller(dayOfWeek,None)
					monthStart = system.date.addMonths(monthStart,1)
					dayOfWeek = nextDayOfWeek(monthStart, weekday)
					dayOfWeek = system.date.addDays(dayOfWeek, ((pattern-3)*7))

