	#Returns the next date that is that weekday
	#date: the datetime to start from
	#weekday: the weekday as a number
	def nextDayOfWeek(date, weekday):
		dayOfWeek = system.date.getDayOfWeek(date)
		daysApart = weekday - dayOfWeek
		if daysApart < 0: daysApart = 7 + daysApart
		return system.date.addDays(date, daysApart)


	schedule = self.custom.schedule
	seasonStart = value['seasonStart']
	startDayOfWeek = system.date.getDayOfWeek(seasonStart)
	seasonEnd = value['seasonEnd']
	if system.date.isBefore(seasonEnd, seasonStart): return 'ERROR: Season End before Start!!!'
	#Pattern Key: 1 = Every, 2 = Every Other, 3 = Every 1st ...7 = Every 5th
	pattern = self.custom.pattern
	#Weekdays Key: 1 = Sunday ... 7 = Saturday
	weekdays = value['weekdays']
	dates = []
	currentDate = system.date.format(system.date.now(),'MM-dd-yyyy')
	#Every
	if pattern == 1:
		for weekday in weekdays:
			schedDay = nextDayOfWeek(seasonStart,weekday)
			while system.date.isBefore(schedDay, seasonEnd):
				dates.append(schedDay)
				schedDay = system.date.addDays(schedDay, 7)
	if pattern == 2:
		for weekday in weekdays:
			schedDay = nextDayOfWeek(seasonStart,weekday)
			while system.date.isBefore(schedDay, seasonEnd):
				dates.append(schedDay)
				schedDay = system.date.addDays(schedDay, 14)
	if pattern > 2:
		for weekday in weekdays:
			monthStart = system.date.getDate(system.date.getYear(seasonStart), system.date.getMonth(seasonStart), 1)
			dayOfWeek = nextDayOfWeek(monthStart, weekday)
			dayOfWeek = system.date.addDays(dayOfWeek, ((pattern-3)*7))
			while system.date.isBefore(dayOfWeek, seasonEnd):
				if system.date.isAfter(dayOfWeek, seasonStart) and system.date.getMonth(dayOfWeek)==system.date.getMonth(monthStart):
					dates.append(dayOfWeek)
				monthStart = system.date.addMonths(monthStart,1)
				dayOfWeek = nextDayOfWeek(monthStart, weekday)
				dayOfWeek = system.date.addDays(dayOfWeek, ((pattern-3)*7))

	for date in dates:
		if system.date.format(date,'MM-dd-yyyy') >= currentDate:
			day = system.date.getDayOfYear(date)
			if day not in schedule: schedule.append(day)

	# append scheduled dates from database
	result = system.db.runNamedQuery('Event/ParticipantEvents/Registration/Get_ScheduledEvents',{'eventId':self.params.event_id})	
	startDate_dayOfYear = system.date.format(self.getChild("root").getChild("FlexContainer").getChild("Left_Flex").getChild("StartsOn_Flex").getChild("Starts_On").props.params.date,'yyyy-MM-dd hh:mm:ss')
#	return startDate_dayOfYear
	weekdays = self.custom.EventData.dateSelectionDays
	if result.getRowCount() > 0:
		for i in range(0,result.getRowCount()):
			startsOn = result.getValueAt(i,'startsOn')
			if (system.date.getDayOfWeek(startsOn) not in weekdays) or (system.date.format(startsOn,'yyyy-MM-dd hh:mm:ss') == startDate_dayOfYear):
				dates.append(startsOn)	
	return dates

