# implement your handler here
import traceback
try:
	#Send the dataset to Excel
	filename = 'Participant_Incidents_' + str(system.date.format(system.date.now(),"yyyyMMddHHmmss")) + '.xls'
	dataset = self.props.data
	NewDataSet = []
	headers = ["Incident Time","Location","Incident Description","Physical Injury","Property Damage","Suspension","Suspension Type","Susp Begin","Susp End","Reinstate Required"]

	for i in range(len(dataset)):
		incident_date = None
		sus_begin = None
		sus_end = None
		if dataset[i]['incident_date'] is not None and dataset[i]['incident_date'] != '':
			incident_date = system.date.format(system.date.fromMillis(dataset[i]['incident_date']), 'MM/dd/yyyy hh:mm a')
		if dataset[i]['reinstateRequired'] != None and dataset[i]['reinstateRequired'] != '' and dataset[i]['reinstateRequired'] != -1:
			if dataset[i]['reinstateRequired'] == 0:
				reinstateRequired = 'FALSE'
			if dataset[i]['reinstateRequired'] == 1:
				reinstateRequired = 'TRUE'
		else:
			reinstateRequired = ''
		if dataset[i]['suspension_time_begin'] is not None and dataset[i]['suspension_time_begin'] != '':
			sus_begin = system.date.format(system.date.parse(dataset[i]['suspension_time_begin'],'MMM dd yyyy'), 'MMM dd yyyy')
		if dataset[i]['suspension_time_end'] is not None and dataset[i]['suspension_time_end'] != '':
			sus_end = system.date.format(system.date.parse(dataset[i]['suspension_time_end'],'MMM dd yyyy'), 'MMM dd yyyy')
		NewDataSet.append([incident_date,dataset[i]['incident_location_name'],dataset[i]['incident_description'],dataset[i]['physical_injury'],dataset[i]['property_damage'],dataset[i]['bar_participant'],dataset[i]['suspension_type_name'],sus_begin,sus_end,reinstateRequired])	
	NewDataSet = system.dataset.toDataSet(headers, NewDataSet)

	xlFile = system.dataset.toExcel(False,NewDataSet)
	system.perspective.download(filename, xlFile)
except:   
	logger = system.util.getLogger("Participant Incidents tableToExcel")
	msg = traceback.format_exc()
	logger.error(msg)
