onChange.script:

emvRoom = self.getChild("emvRoom")

if self.custom._isDev:
	if currentValue.value is True:
		facilityId = self.view.params.facilityId
		self.setRoomsDropdown(facilityId)
		emvRoom.props.params.options = []
	else:
		emvRoom.props.params.options = []

emvRoom.props.params.actionPerformedPulse = False

customMethods.script:
roomId = kwargs.get("roomId")
facilityId = kwargs.get("facilityId")
oneDay = kwargs.get('oneDay')
multi_facility = kwargs.get('multi_facility')
payload = {}

if oneDay:
	payload = {'oneDay': oneDay}

if roomId or facilityId:
	if roomId:
		payload['roomId'] = roomId

	if facilityId:
		payload['facilityId'] = facilityId	
if multi_facility:
		payload['multi_facility'] = multi_facility


system.perspective.print(('mhSetLayoutDesigner',payload)) 

system.perspective.sendMessage('mhSetLayoutDesigner', payload = payload)
